#include "include/Katagrafeas.hpp"
#include <iostream>
#include <fstream>
#include <thread>

namespace SomeLibrary
{
  std::ostream out_stream(std::cout.rdbuf());

  void print()
  {
    out_stream << "";
  }
}

namespace OtherLibrary
{
  std::ostream out_stream(std::cout.rdbuf());

  void print()
  {
    out_stream << "";
  }
}

Katagrafeas::Stream out_logger(std::cout);

void print()
{
  out_logger << "________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________";
}

void print2()
{
  out_logger << "----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------";
}


int main()
{
    out_logger.link(SomeLibrary::out_stream);
    out_logger.link(OtherLibrary::out_stream);
    again:
    std::thread t1(SomeLibrary::print);
    std::thread t2(OtherLibrary::print);
    //std::thread t3(print);
    std::thread t4(print2);


    t1.join();
    t2.join();
    //t3.join();
    t4.join();
    std::cin.get();
    goto again;
}